<?phpglobal $ashu_option;if(is_home()){	$keywords = $ashu_option['general']['keywords'];	$description = $ashu_option['general']['description'];}elseif(is_single() || is_page() ){	$description = get_post_meta($post->ID,'description',true);	$keywords = get_post_meta($post->ID,'keywords',true);		if($keywords == ''){		$aaa=wp_get_post_tags($post->ID);		$key_arry = array();		foreach($aaa as $bbb)		$key_arry[] = $bbb->name;		$keywords = implode(',',$key_arry);	}		if($keywords == ''){		$keywords = $ashu_option['general']['keywords'];	}	if($description == ''){		$description = str_replace("\n","",strip_tags($post->post_content));		$description = str_replace("\r","",$description);		$description = str_replace("\t","",$description);		$description = mb_substr( $description,0,200,'UTF-8');	}}elseif( is_tax('softs')||is_tax('topics') ){  $currentterm = get_queried_object();  $cat_id=$currentterm->term_id;	$val=get_option('cat_meta_key_'.$cat_id);	$keywords=$val['metakey'];	$description=$val['description'];	$metas=$val['metas'];      if($description == ''){    $description = get_term_meta($currentterm->term_id , 'description',true);  }  if($description == ''){    $keywords = get_term_meta($currentterm->term_id , 'keywords',true);  }		if($description == ''){		$description = str_replace("\n","",strip_tags(term_description()));	$description = str_replace("\r","",$description);	$description = str_replace("\t","",$description);	$description = mb_substr( $description,0,100,'UTF-8');	}		if($description == ''){		$description = $ashu_option['general']['description'];	}	if($keywords == ''){		$keywords = $ashu_option['general']['keywords'];	}}elseif( is_post_type_archive('soft') || is_post_type_archive('topic') ){	$description = $ashu_option['general']['description'];	$keywords = $ashu_option['general']['keywords'];}elseif( is_category() ){	$currentterm = get_queried_object();	$description = get_term_meta($currentterm->term_id , 'description',true);	$keywords = get_term_meta($currentterm->term_id , 'keywords',true);		if($description == ''){		$description = str_replace("\n","",strip_tags(term_description()));	$description = str_replace("\r","",$description);	$description = str_replace("\t","",$description);	$description = mb_substr( $description,0,100,'UTF-8');	}		if($description == ''){		$description = $ashu_option['general']['description'];	}	if($keywords == ''){		$keywords = $ashu_option['general']['keywords'];	}}else{	$keywords = $ashu_option['general']['keywords'];	$description = $ashu_option['general']['description'];}?>